import java.security.MessageDigest;

public class Foo {
    private static final MessageDigest INSTANCE = MessageDigest.getInstance("SHA-512");
    void bar() {
        byte[] input = new byte[13];
        INSTANCE.digest(input); // poor, no thread-safety
    }
}

import java.security.MessageDigest;

public class Foo {
    private static final MessageDigest INSTANCE = MessageDigest.getInstance("SHA-512");
    void bar() {
    }
}

import java.security.MessageDigest;

public class Foo {
    private static final MessageDigest INSTANCE = MessageDigest.getInstance("SHA-512");
    synchronized void bar() {
        byte[] input = new byte[13];
        INSTANCE.digest(input); // no thread-safety, protected by no static monitor
    }
}

import java.security.MessageDigest;

public class Foo {
    private static final MessageDigest INSTANCE = MessageDigest.getInstance("SHA-512");
    void bar() {
        synchronized(INSTANCE) {
            byte[] input = new byte[13];
            INSTANCE.digest(input); // no thread-safety, protected by no static monitor
        }
    }
}
